                                        {% if validationRequest.progress==100 %}
                                            <button class=\"btn green disabled\">Verification Terminer</button>
                                            {% else %}
                                            <a href=\"{{ url('validator_verify_version',{token:validationRequest.token}) }}\" class=\"btn green\">Verifier la version</a>
                                        {% endif %}
                                    </td>
                                </tr>
                            {% endfor %}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
